String Page_set_Wifi = R"====(<!DOCTYPE html><html lang="es"><head><meta charset="UTF-8"><title>Set YourClock WiFi</title></head><body><div id="app"><div id="header"><button id="button_exit"></button></div><div id="forms"><div id="form_wifi"><div id="title_ssid">SSID:</div><input id="input_ssid" type="text"><div id="title_password">PASSWORD:</div><input id="input_password" type="password"><button id="button_save" class="button">GUARDAR</button><p id="text_response_wifi"></p></div><div id="form_scan"><button id="button_scan" class="button">ESCANEAR</button><div id="text_response_scan"></div><ol type="1" id="list_wifi"></ol></div></div></div><script>const buttonScan=document.getElementById("button_scan"),buttonSave=document.getElementById("button_save"),buttonExit=document.getElementById("button_exit"),inputPassword=document.getElementById("input_password"),inputSsid=document.getElementById("input_ssid"),listWifi=document.getElementById("list_wifi"),textResponseWifi=document.getElementById("text_response_wifi"),textResponseScan=document.getElementById("text_response_scan");async function request(e,t,n=null){var s={method:e,body:n};try{var i=await fetch(t,s);return{body:await i.text(),statusCode:i.status,statusText:i.statusText}}catch(e){return console.error(e),{body:e,statusCode:0,statusText:"ERROR"}}}async function scan(){startOperation(buttonScan);let e=await request("GET","http://%ip/scan");for(textResponseScan.innerHTML="";listWifi.firstChild;)listWifi.removeChild(listWifi.firstChild);if(200==e.statusCode){endOperation(buttonScan,"success_operation");var t=JSON.parse(e.body).networks;textResponseScan.innerHTML="Estas son las redes WiFi mas cercanas a su dispositivo:";for(let e=0;e<t.length;e++){var n=document.createElement("li");n.appendChild(document.createTextNode(t[e])),listWifi.appendChild(n)}}else 204==e.statusCode?textResponseScan.innerHTML="No hay redes WiFi disponibles en este momento":(endOperation(buttonScan,"failed_operation"),textResponseScan.innerHTML="Hubo un error al escanear las redes WiFi, verifique la conexion y vuelva a intentarlo")}async function save(){startOperation(buttonSave),textResponseWifi.innerHTML="",200==(await request("POST",`http://%ip/save?ssid=${inputSsid.value}&pass=${inputPassword.value}`)).statusCode?(endOperation(buttonSave,"success_operation"),textResponseWifi.innerHTML="Credenciales guardadas exitosamente en el dispositivo"):(endOperation(buttonSave,"failed_operation"),textResponseWifi.innerHTML="Hubo un error al guardar las credenciales en el dispositivo, verifique la conexion y vuelva a intentarlo")}async function exit(){200==(await request("GET","http://%ip/restart")).statusCode&&(window.open("","_parent",""),window.close())}function startOperation(e){e.setAttribute("disabled","true"),e.classList.add("start")}function endOperation(e,t){let n=e.innerHTML;e.innerHTML="",e.classList.remove("start"),e.classList.add(t),setTimeout((function(){e.classList.remove(t),e.innerHTML=n,e.removeAttribute("disabled")}),1250)}buttonScan.addEventListener("click",scan),buttonSave.addEventListener("click",save),buttonExit.addEventListener("click",exit);</script><style>#app,#forms{flex-direction:column;display:flex}#button_exit::after,#button_exit::before{content:" ";position:absolute;background-color:#797979;transform:rotate(45deg)}#app,#forms,#header{display:flex}.button,input:focus,input:hover{box-shadow:1px 4px 8px 1px #00000033,1px 6px 20px 1px #00000030}.button,input{color:#858585;letter-spacing:1px;transition:.5s}#button_exit,.button,input{transition:.5s}#header{justify-content:flex-end}#button_exit{border-radius:50%;width:40px;height:40px;border:1px solid #797979;position:relative;background:#f1f1f1}#button_exit:hover{border:0;box-shadow:1px 4px 8px 1px #00000066,1px 6px 20px 1px #00000063;background:#ff4c4c}#button_exit:hover::after,#button_exit:hover::before{background-color:#f1f1f1;box-shadow:1px 4px 8px 1px #00000066,1px 6px 20px 1px #00000063}#button_exit::before{width:3px;left:17px;top:5px;bottom:5px}#button_exit::after{height:3px;top:17px;left:5px;right:5px}#forms{align-items:center;justify-content:center}#form_scan,#form_wifi{display:flex;flex-direction:column;text-align:center;width:300px;border-radius:25px;padding:40px 48px;box-shadow:0 5px 9px 0 #00000033,0 7px 21px 0 #00000030;margin-bottom:30px}#text_response_scan{margin-top:25px}#text_response_wifi{margin-top:25px;margin-bottom:0}input{height:30px;font-size:16px;padding-left:5px;padding-bottom:0;margin-bottom:30px;border:0;background:#f0f0f0;border-radius:4px}input:focus,input:hover{background:#f0f0f0;border:0;outline:0}.button{height:40px;text-align:center;border-radius:40px;background:#f3f3f3;border:0;text-shadow:0;font-size:12px;font-weight:700;cursor:pointer}.button:hover{color:#333;background:#e6e6e6}.success_operation,.success_operation:hover{color:#fff;background:#88ff8e;box-shadow:1px 4px 8px 1px #00ff0033,1px 6px 20px 1px #00ff0030}.start{margin-left:calc(50% - 15px);width:40px;font-size:0;animation:2s linear .25s infinite rotating;cursor:default}.failed_operation,.success_operation{border:0;font-size:13px}.failed_operation,.failed_operation:hover{box-shadow:1px 4px 8px 1px #ff000033,1px 6px 20px 1px #ff000030;color:#fff;background:#f88}.success_operation:after{content:"\2713"}.failed_operation:after{content:"\2718"}@keyframes rotating{from{transform:rotate(0)}to{transform:rotate(360deg)}}</style></body></html>)====";